INSERT INTO `couch_fields` VALUES("16", "7", "more_ways_to_give", "Other giving options", "", "richtext", "0", "text", "0", NULL, "\n				<h4>\n					More ways to give\n				</h4>\n\n				<ul>\n					<li>\n						<p>\n						<a href=\"mailto:joleneb@rays.org?subject=Volunteering+with+RAYS?\">Volunteer</a> your time with us.\n						</p>\n					</li>\n\n					<li>\n						<p>\n\n						Donate by mail by sending a check payable to\n						<em>RAYS</em> to Renton Area Youth & Family Services,\n						PO Box 1510, Renton WA 98057. Or donate by phone by\n						calling (425) 271-5600.\n						\n						</p>\n					</li>\n\n					<li>\n						<p>\n\n						Contribute through your workplace giving campaign\n						(e.g. the United Way campaign) by designating RAYS on\n						your contribution form.\n						\n						</p>\n					</li>\n\n					<li>\n						<p>\n\n							Contribute through estate planning, wills, or\n							donating stock.\n						\n						</p>\n					</li>\n\n					<li>\n						<p>\n\n							Contribute through the <a\n							href=\"http://rentonfoundation.org/\">Renton\n							Community Foundation</a> by designating <em>Renton\n							Area Youth & Family Services</em> as your\n							\"donation type\".\n						\n						</p>\n					</li>\n\n\n				</ul>\n\n				", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'more_ways_to_give\'\r\nlabel=\'Other giving options\'\r\ntype=\'richtext\'/>", "", NULL);
INSERT INTO `couch_fields` VALUES("18", "8", "kincare_overview", "Kincare overview", "", "richtext", "0", "text", "2", NULL, "\n\n				<h4>Kinship families</h4> \n\n				<p>\n					The Kincare Support Program serves kinship families. These are families where\n					a relative caregiver—usually a grandmother—is the primary caregiver of\n					children under age 18. Many of the children have ended up in these situations\n					through traumatic experiences, such as the death, incarceration, or substance\n					abuse of a parent. Kinship families often struggle with the impact of trauma\n					on children as well as poverty and issues related to aging for the caregivers.\n					Many kinship providers report losing their friends and social networks once\n					they become primary caregivers again—a situation that can lead to feelings of\n					alienation and profound loss. The Kincare Support Program links these families\n					to services and provides support so that the caregivers can be effective\n					parents to their young children.\n				</p>\n\n				<h4>How we support these families</h4>\n\n				", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'kincare_overview\'\r\nlabel=\'Kincare overview\'\r\ntype=\'richtext\'\r\norder=\'2\'/>", "", NULL);
INSERT INTO `couch_fields` VALUES("25", "8", "kincare_further_info", "Kincare continued information", "Content that comes after the expandable sections", "richtext", "0", "text", "4", NULL, "\n\n				<h4>A larger trend with proven results</h4>\n\n				<p>\n\n					Kinship care is one of the fastest growing trends in the child welfare system\n					and—as an alternative to non-kin foster placements—can lead to improved\n					outcomes for children. For example, according to a study by the Annie E. Casey\n					Foundation, kinship care helps keep families together, provides a more stable\n					alternative for children, and helps utilize and preserve cultural values,\n					especially for children of color. Additionally, children in kinship homes have\n					fewer behavioral problems, are less likely to reenter care after they’ve gone\n					home or been adopted, and are three to four times less likely to be maltreated\n					than children in traditional foster homes.  A Johns Hopkins study reported\n					that having grandparents as caregivers reduces the risk of childhood injury\n					(the leading cause of death for children) by about half. By conservative\n					estimates, kinship caregivers save taxpayers more than $6.5 billion per year\n					in federal foster care costs.\n\n				</p>\n\n				", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'kincare_further_info\'\r\nlabel=\'Kincare continued information\'\r\ntype=\'richtext\'\r\norder=\'4\'\r\ndesc=\'Content that comes after the expandable sections\'/>", "", NULL);
INSERT INTO `couch_fields` VALUES("26", "3", "agency_history", "Agency history", "Warning: be careful around the images to keep formatting intact", "richtext", "0", "text", "0", NULL, "\n\n\n			<h2>\n				Agency history\n			</h2>\n\n			<figure class=\"img-float img-float-right\">\n				<img src=\"image/berry_house_001.jpg\" >\n				<figcaption>\n					The original Berry House, 1970.\n				</figcaption>\n			</figure>\n\n			<p>\n\n				Renton Area Youth and Family Services (RAYS) opened in the\n				spring of 1970. At that time, existing agencies were largely\n				adult-focussed; there were few services available for youth\n				and families in Renton and South King County. The need for\n				youth-specific services was large and growing due to the\n				emerging drug use and related mental health issues for this\n				age group. The agency operated the Berry House, which was a\n				community drop in center. The purpose of the Berry House was\n				to provide crisis intervention and support services to youth\n				and families who were impacted by drug use. The program was\n				developed and administered under the auspices of the Renton\n				School District, working collaboratively with many community\n				groups. The Berry House was staffed by volunteers who provided\n				youth and families with a place to talk over their concerns\n				and be referred to other very limited resources.\n			\n			</p>\n\n			<p>\n				\n				In 1971, Berry House services were moved from their original\n				location near Renton High School to school district facilities\n				on N. 4th Street in Renton. At this time, the agency Advisory\n				Board wished to create a broader base of services to more\n				effectively address the needs of its clients. With funding\n				support from a federal grant, the program, now called the\n				School and Community Drug Intervention Program, expanded to\n				provide, professionally staffed services in the area of crisis\n				intervention, counseling, foster care, job placement\n				assistance, and community education services. In recognition\n				of this expanded service focus, the name of the agency changed\n				to Renton Area Youth Services, or RAYS, in May 1972. The\n				agency also began to receive funding support from the City of\n				Renton and King County. Following the expiration of the\n				federal grant and after reviewing future funding options the\n				Advisory Board determined that RAYS should become a private,\n				non-profit agency and in June 1975, this change in\n				organization status took place.\n\n			</p>\n\n			<h3>\n				Family Focused Services\n			</h3>\n\n				<img src=\"image/steve_1987.jpg\" class=\"img-float img-rounded img-float-left\">			\n\n			<p>\n\n				The program continued to emphasize services to youth and\n				families who were impacted by drug use (RAYS was and is a\n				Washington State licensed substance abuse treatment agency),\n				but the program focus gradually began to broaden to serve\n				youth and families with a variety of presenting problems\n				including family conflict, school problems, mental health\n				issues, and physical and sexual abuse. Increasingly, the\n				program staff worked with entire families as well as extended\n				families, recognizing the family system role in behavior\n				change. The agency became more and more involved with\n				prevention and early intervention services and began to work\n				with pre-teens and children more frequently. Our school based\n				counseling services continued to expand in Renton and Tukwila\n				to promote service accessibility for children and families.\n				The agency programs in the area of foster care and employment\n				were discontinued due to changes in program emphasis.\n\n			</p>\n\n			<p>\n			\n				In 1984, the agency relocated into the Renton Area Multi\n				Service Center in downtown Renton, a registered historic site,\n				and one of the current agency service sites. As the agency\n				moved through the 1980’s and  into the 1990’s, its mental\n				health service capacity continued to expand and the service\n				population continued to broaden as we addressed a regional\n				need for services to children under 12 years of age. While the\n				agency continued to serve adolescents, early intervention and\n				prevention oriented services with children as young as age 4\n				became increasingly significant as part of a more family\n				focused, early intervention service philosophy. This addition\n				to our services resulted in a change in the agency name to\n				Renton Area Youth and Family Services in 1988.\n\n			</p>\n\n			<img src=\"image/royal_hills_1988_1.jpg\" class=\"img-float img-rounded img-float-right\">\n\n			<p>\n\n				Renton Area Youth and Family Services has undergone many\n				changes since its Berry House beginnings in 1970. What has\n				remained constant throughout the agency’s existence is its\n				commitment to serve children, youth and families in need\n				through the provision of effective, quality, accessible\n				services. The agency maintains a strong sense of connectedness\n				to the communities it serves in many ways such as through\n				community partnerships with schools, churches, and community\n				service groups, but importantly through its Board of Directors\n				which is composed of persons who have a vital interest in the\n				life of the community.  In response to an ever-growing\n				community need for its services, the agency has broadened its\n				service area to include Renton, Maple Valley, and Tukwila.\n				Funding for services is provided by the cities of Renton and\n				Tukwila, The United Way, King County Government, Medicaid\n				mental health, foundation grants, contributions and fees for\n				services.\n\n			</p>\n\n			<p>\n\n				As RAYS moved toward its twenty-fifth year of existence in\n				1995, certain hallmarks were evident. The agency had been and\n				continued to be provider of quality services that address\n				particular needs of children, youth and families; the agency\n				continued to respond dynamically to the long term and emerging\n				needs of the community. In 1995, the agency became a\n				Washington State  licensed mental health provider for case\n				management and outpatient services, expanded its school-based\n				services to include mental health, youth development, and drug\n				treatment services and in this way, made services more\n				accessible to children and families. The West Hill Family\n				Enrichment Center opened in 1996 to provide a range of family\n				oriented prevention services to the Skyway, Campbell Hill,\n				Lakeridge and Bryn Mawr communities. Specialized youth\n				development services (SPARK T.H.I.S.) are also available at\n				this site.\n\n			</p>\n\n			<p>\n\n				The new millennium brought continuing challenges to RAYS as\n				the communities we serve have become significantly more\n				economically, culturally, and ethnically diverse. The agency\n				is called upon to respond to community needs in ways that are\n				mission-driven, creative, effective, and culturally\n				appropriate. RAYS continues to be in conversation with our\n				community, our funding groups, and our agency partners to\n				explore the best ways to be responsive and to significantly\n				impact the lives of children, youth, and families. One of\n				these opportunities is the Affordable Care Act of 2014, which\n				brings with it the opportunity to form new partnerships with\n				primary health care providers that offers coordinated care for\n				our clients who are without health care. Additionally, our\n				Healthy Start and Kincare programs serve young, first time\n				parents and grandparents raising grandchildren respectively\n				Our history indicates repeatedly that, working in concert with\n				our community and with individuals and groups who are\n				concerned with and invested in the quality of life for\n				families, we can continue to build positive futures for\n				children.\n\n			</p>\n\n\n		</div>\n	</div>\n	<!-- end main -->\n\n	", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'agency_history\'\r\nlabel=\'Agency history\'\r\ntype=\'richtext\'\r\ndesc=\'Warning: be careful around the images to keep formatting intact\'/>", "", NULL);
