INSERT INTO `couch_fields` VALUES("30", "6", "mission_vision", "Mission & vision text", "", "richtext", "0", "text", "2", NULL, "\n\n				<p>\n					Is to strengthen the lives\n					of children, youth and families by restoring hope and stability in the face of\n					emotional conflict, substance abuse and poverty.  In partnership with our\n					community, we transform the lives of kids and families through comprehensive\n					counseling, training and support.\n				\n				</p>\n				<p>\n					<strong style=\"font-size: 140%;\">Our vision</strong> is for all youth and families in our\n					community to have the resources, support and strength they need to recover from\n					substance abuse, mental health issues and other serious life challenges.\n				</p>\n\n				", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'mission_vision\'\r\nlabel=\'Mission & vision text\'\r\ntype=\'richtext\'\r\norder=\'2\'/>", "", NULL);
