INSERT INTO `couch_fields` VALUES("39", "13", "family_center_overview", "Family Center overview", "", "richtext", "0", "text", "2", NULL, "\n\n				<h2>Family Support & Prevention Services</h2>\n\n				<p>\n\n					With the presense of the Family Center atop the West Hill, RAYS seeks to\n					support the growth and development of families and all\n					their members - adults, youth, and children alike - to\n					build healthier families and ultimately a stronger\n					community. Staff and families work together in\n					relationships based on mutual equality and respect, while\n					programs are flexible and continually responsive to\n					emerging issues.\n\n				</p>\n				\n				<h4>Our programs & services</h4>\n\n				<p>\n\n					The West Hill community recognizes the RAYS Family Center as\n					a community space where they can go for a multitude of\n					programs and services, or simply as a place to get out of the\n					cold. More specifically the Family Center is home to or offers:\n\n				</p>\n\n				", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'family_center_overview\'\r\nlabel=\'Family Center overview\'\r\ntype=\'richtext\'\r\norder=\'2\'/>", "", NULL);
