INSERT INTO `couch_fields` VALUES("73", "11", "playandlearn_story_1", "Play & Learn story 1", "Warning: be extremely careful with any changes to keep formatting intact", "richtext", "0", "text", "8", NULL, "\n\n			<h3>Stories of our clients</h3>\n\n			<article id=\"story-1\" name=\"story-1\" class=\"story\">\n\n				<h4>Socializing for two - <em>Millie & Arlene</em></h4>\n\n				<p>\n\n					Prior to joining the Kaleidoscope Play & Learn group at RAYS, Millie spent\n					every day at her grandmother’s house once her parents left for work each\n					morning. Millie had only ever interacted with adults and had rarely been\n					around other children which led to a very extreme insecure attachment to her\n					grandmother. Millie’s grandmother, Arlene, introduced her granddaughter to the\n					group in hopes of socializing Millie with other children and to learn school\n					readiness techniques to practice at home. Arlene hoped the exposure to other\n					children would help Millie become comfortable with leaving her grandmother’s\n					side. \n\n				</p>\n				<p class=\"quote-text\">\n\n					<blockquote class=\"quote-right\">\n						Play & Learn provided Arlene with the opportunity to communicate with other\n						caregivers, and to gain new perspectives as well as relationships.\n					</blockquote>\n\n					RAYS Kaleidoscope Play & Learn group introduced a structure to\n					Millie’s life that she had not ever experienced. Millie quickly learned to\n					connect the shape and color vocabulary she had mastered with her family at\n					home with different sensory stations during child-directed play time. As a\n					former pre-school teacher, Arlene brought many great ideas to the group. Play\n					& Learn provided Arlene with the opportunity to communicate with other\n					caregivers and grandparents and she was subsequently able to gain new\n					perspectives as well as relationships. The Play & Learn facilitators\n					provided Arlene with suggestions and techniques which ultimately helped Millie\n					become more securely attached to her grandmother. \n\n				</p>\n				<p>\n\n					Millie and Arlene faithfully\n					attended Play & Learn for over a year. Millie has recently left Play and\n					Learn because she has been enrolled in preschool. Play & Learn was crucial\n					in helping Millie develop school readiness skills such as sharing, self-\n					regulation, and structured time. However, the Kaleidoscope Play & Learn\n					group has also proved to be just as beneficial to caregivers alike. Arlene\n					gained the confidence in her ability as a caregiver to encourage a healthier\n					form of attachment in Millie.\n\n\n				</p>\n\n				<p class=\"asterisk-text\">\n					*Names changed for confidentiality purposes.\n				</p>\n\n			</article>\n\n			", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'playandlearn_story_1\'\r\nlabel=\'Play & Learn story 1\'\r\ntype=\'richtext\'\r\norder=\'8\'\r\ndesc=\'Warning: be extremely careful with any changes to keep formatting intact\'/>", "", NULL);
INSERT INTO `couch_fields` VALUES("74", "11", "playandlearn_overview", "Play & Learn overview", "", "richtext", "0", "text", "7", NULL, "\n\n			<h2>Play & Learn</h2>\n\n			<h4>Learning for kids & adults alike</h4>\n\n			<p> \n				The RAYS Family Center offers a Kaleidoscope Play & Learn group once a week. \n				Play & Learn is a county-wide program, with all groups following a general set \n				of guidelines (which can be found\n				<a href=\"http://www.childcare.org/ffn-care/play-and-learn-groups.asp\">here</a>). \n				As described on the Child Care Resources website:\n			</p>\n\n			<p class=\"indent-quote\">\n				<em>\n					\"Kaleidoscope Play & Learn groups are organized play groups\n					for young children and the people who take care of them – grandparents, aunts\n					and uncles, brothers and sisters, parents and other family members and\n					friends.\"\n				</em>\n			</p>\n			<p class=\"indent-quote\">\n				<em>\n					\"At Kaleidoscope Play & Learn, children and caregivers take part in\n					play activities that support children’s early learning. They also get a chance\n					to meet new friends, hear more about other early learning and family support\n					programs in their neighborhood, and to get out of the house and have fun.\n					Caregivers also learn more about child development and what they can do at\n					home to support the healthy growth and learning of the children in their care.\"\n				</em>\n			</p>\n\n			", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'playandlearn_overview\'\r\nlabel=\'Play & Learn overview\'\r\ntype=\'richtext\'\r\norder=\'7\'/>", "", NULL);
