INSERT INTO `couch_fields` VALUES("76", "17", "thankyou", "Donation thank you message", "", "richtext", "0", "text", "1", NULL, "\n\n				<h2>Thank you, your donation is complete</h2>\n\n				<p>\n\n					RAYS relies on its individual\n					donors to support its efforts within the community. A\n					receipt will be emailed to you, and you can also log into\n					your account at <a\n					href=\"http://www.paypal.com/us\">www.paypal.com/us</a> to\n					view details of this transaction.\n				\n				</p>\n\n				<p>\n					Every donation makes a difference.\n				</p>\n\n				", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'thankyou\'\r\nlabel=\'Donation thank you message\'\r\ntype=\'richtext\'\r\norder=\'1\'/>", "", NULL);
