INSERT INTO `couch_fields` VALUES("120", "19", "sparkthis_overview", "Spark T.H.I.S. overview", "", "richtext", "0", "text", "9", NULL, "\n\n					<h3 id=\"spark-title\">Truth, Hope, Involvement, Success</h3>\n\n					<h4>Our mission</h4>\n\n					<p>\n					Spark T.H.I.S. promotes student success in school and in the community by providing one-on-one coaching, group support, and skill building opportunities for middle and high school youth, with a high quality programming standard.\n					</p>\n\n					<h4>What we do</h4>\n\n					<p>\n						We work within the Renton School District, building on resources provided by teachers, school counselors and other school staff. We believe that good relationships with school staff are essential to maximizing students’ opportunities to succeed in school. We believe that family members can and should play a key role in their children\'s school success, and so we try to engage families whenever possible. If a supportive adult family member is not available, we will work with the youth to identify and establish a strong relationship with a supportive adult outside the family.\n					</p>\n\n					<p>\n						We believe that diversity enriches the school environment, and so we help youth to celebrate and appreciate diversity, and their own unique heritage. Youth connections with the school (and greater) community contribute to staying in school and ultimately graduating. We work with youth to increase participation in school and extracurricular activities, along with community volunteer work.\n					</p>\n\n\n\n					<h4>How we do it</h4>\n\n					", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'sparkthis_overview\'\r\nlabel=\'Spark T.H.I.S. overview\'\r\ntype=\'richtext\'\r\norder=\'9\'/>", "", NULL);
