INSERT INTO `couch_fields` VALUES("137", "16", "home_overview", "Short homepage RAYS description", "", "richtext", "0", "text", "9", NULL, "\n\n			<h1 id=\"home-title\">Renton Area Youth & Family Services</h1>\n\n			<p>\n\n				RAYS is a community-oriented nonprofit organization that provides a\n				variety of supportive services to children, youth, and families who live\n				in the greater Renton and Tukwila areas. We provide mental health\n				counseling for children and youth grappling with trauma, both in schools\n				at at our own services sites, and offer drug treatment counseling to teens\n				trying to chart healthier courses in their lives.\n\n			</p>\n\n			<p>\n\n				RAYS operates school and community-based services for middle and high\n				school youth that focus on building positive life skills, staying in\n				school, and realizing goals. RAYS also offers parenting support services\n				for young parents, as well as for grandparents raising their\n				grandchildren. Caregivers of all ages have turned to RAYS for more than 40\n				years for support and shoulder to lean on.\n\n			</p>\n\n			<h3 class=\"home-sub-title\">Do you believe that every child has potential?</h3>\n\n			<p>\n\n				Consider <a href=\"donate.php\">making a donation</a> to RAYS. Donate in\n				honor of someone you know who struggled as a child, and for the success\n				that fulfills them as an adult. Or donate for the child you know who\n				struggles today, and for the success that they can find if given the\n				chance.\n\n			</p>\n\n			<br>\n\n			<h4 id=\"supporters-title\">RAYS is proud to be supported by: </h4>\n\n			", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'home_overview\'\r\nlabel=\'Short homepage RAYS description\'\r\ntype=\'richtext\'\r\norder=\'9\'/>", "", NULL);
