INSERT INTO `jstitch_posts` VALUES("21", "1", "2013-09-09 01:18:39", "2013-09-09 01:18:39", "Specs specs specs", "Quinny Vest", "Meet the Quinny vest, named in honor of its original inspiration. Made of easy care, ultra-soft recycled Polartec fleece, designed in a little studio on Bainbridge Island. Each piece is sewn by elves Americans in a nice factory in Seattle.\r\n\r\nSpecial features include hand pockets in front, and in the back, a surprise! Check out the built-in backpack. It’s perfect for your little one to carry a spare diaper and wiper, binky and stinky, or perhaps a lucky bird feather or a snack. Also included: a leak-proof pouch or lining(?) (Text of the link hover “It’s heavy doody”)", "publish", "closed", "closed", "", "woop", "", "", "2013-09-29 06:02:35", "2013-09-29 06:02:35", "", "0", "http://localhost/jstitch/wordpress/?post_type=product&#038;p=21", "0", "product", "", "0");
