INSERT INTO `wp_posts` VALUES("5", "1", "2013-08-23 20:31:34", "2013-08-23 20:31:34", "\r\n<!-- <div class=\"background-block\"> -->\r\n<div id=\"full-width-slider\" class=\"royalSlider heroSlider rsMinW\">\r\n	<div class=\"rsContent\">\r\n		<img class=\"rsImg\" src=\"img/pw-exterior.jpg\" alt=\"\">\r\n					<!-- <div class=\"infoBlock infoBlockLeftBlack rsABlock\" data-fade-effect=\"\" data-move-offset=\"10\" data-move-effect=\"bottom\" data-speed=\"200\">\r\n						<h4 style=\"color: white;\">Welcome</h4>\r\n					</div> -->\r\n				</div>\r\n				<div class=\"rsContent\">\r\n					<img class=\"rsImg\" src=\"img/pw-interior1.jpg\" alt=\"\">\r\n					<!-- <div class=\"infoBlock  rsAbsoluteEl\" style=\"color:#000;\" data-fade-effect=\"\" data-move-offset=\"10\" data-move-effect=\"bottom\" data-speed=\"200\">\r\n						<h4>This is a static HTML block</h4>\r\n						<p>It\'s always displayed and not animated by slider.</p>\r\n					</div> -->\r\n				</div>\r\n				<div class=\"rsContent\">\r\n					<img class=\"rsImg\" src=\"img/pw-interior2.jpg\" alt=\"\">\r\n					<!-- <div class=\"infoBlock rsABlock infoBlockLeftBlack\" data-fade-effect=\"\" data-move-offset=\"10\" data-move-effect=\"bottom\" data-speed=\"200\">\r\n						<h4>You can link to this slide by adding #3 to url.</h4>\r\n						<p><a href=\"http://dimsemenov.com/plugins/royal-slider/gallery-with-deeplinking/\">Learn more</a></p>\r\n					</div> -->\r\n				</div>\r\n			</div>\r\n			<!-- </div> -->\r\n\r\n			<!-- <img src=\"img/pw-exterior.jpg\" class=\"image-border\" /> -->\r\n			<!-- <img src=\"img/pw-diptych.jpg\" class=\"image-border\" /> -->\r\n			<!-- <img src=\"img/rainier.jpg\" class=\"image-border\"/> -->\r\n\r\n			<div class=\"block-text\">\r\n\r\n				<h3>Welcome to the pacific northwest</h3>\r\n\r\n				<p>\r\n\r\n					Retreat to the privacy of a modern cottage designed by Jetson\r\n					Luddite Architects and featured in Coastal Living and Pacfic\r\n					Northwest Magazines. Located on Point White, Bainbridge\r\