INSERT INTO `wp_posts` VALUES("78", "1", "2013-08-23 22:58:51", "2013-08-23 22:58:51", "[new_royalslider id=\"1\"]\r\n\r\n<div class=\"block-text\">\r\n<h3>Welcome to the pacific northwest</h3>\r\n<p>Retreat to the privacy of a modern cottage designed by Jetson Luddite Architects and featured in Coastal Living and Pacfic Northwest Magazines. Located on Point White, Bainbridge Island’s best no-bank beach, the cottage is just steps away from breathtaking views of Mount Rainier, Rich Passage and Blake Island. The yard is professionally landscaped and the beach is accessed by a garden path.</p>\r\n\r\n<p>Amenities include a queen size bed and four twin bunks, a full bath, washer and dryer, fluffy terry robes and fresh linens. There is an abundance of materials for reading, writing, sketching, stargazing and dreaming. The cottage is set in a secluded garden and shares 180 feet of waterfront. It’s a birder’s paradise, with kingfishers, redwing blackbirds, eagles, osprey, blue herons, pipers, swallows and songbirds. You might encounter otters, sea lions and even the occasional orca pod.</p>\r\n\r\n</div>", "Home", "", "inherit", "open", "open", "", "4-revision-v1", "", "", "2013-08-23 22:58:51", "2013-08-23 22:58:51", "", "4", "http://localhost/pw-house/wordpress/4-revision-v1/", "0", "revision", "", "0");
