INSERT INTO `wp_posts` VALUES("142", "1", "2013-08-24 07:21:20", "2013-08-24 07:21:20", "<img alt=\"\" src=\"/pw-house/wordpress/wp-content/themes/pointwhite/img/pw-beach-wide.jpg\" />\n<img src=\"http://localhost/pw-house/wordpress/wp-content/uploads/2013/08/pw-beach-wide.jpg\" alt=\"pw-beach-wide\" width=\"1200\" height=\"375\" class=\"alignnone size-full wp-image-105\" />\n<div class=\"block-text\">\n<h2>A wealth of things to do</h2>\nHike the shore-to-forest trails of the neighborhood or jump on your mountain bike. Walk along the scenic beach or ride the double bike to shops, restaurants, entertainment and amenities including a gourmet bakery and deli, a luxurious day spa, live music at The Treehouse Café, a neighborhood convenience shop, the 1912 Manor House and five-star Beach House Restaurant, the Pleasant Beach Village and the Historic Lynnwood Theatre, a vintage venue dating back to 1936. Enjoy the seasonal Sunday Market in the summer months, pick wild berries and apples in the fall, or settle in for winter stormwatching.\n<h4>Activities</h4>\n<ul class=\"text-cols--2\">\n	<li>Fire Pit</li>\n	<li>Mountain Biking</li>\n	<li>Fort Ward State Park</li>\n	<li>Golf</li>\n	<li>Horseback Riding</li>\n	<li>Wildlife Viewing</li>\n	<li>Swimming</li>\n	<li>Equestrian Events</li>\n	<li>Sound/Bay Fishing</li>\n	<li>Sailing</li>\n	<li>Cycling</li>\n	<li>Hiking</li>\n	<li>Kayaking</li>\n	<li>Walking</li>\n	<li>Health/Beauty Spa</li>\n	<li>Winery Tours</li>\n	<li>Botanical Garden</li>\n	<li>Cinemas</li>\n	<li>Marina</li>\n	<li>Library</li>\n	<li>Live Theater</li>\n	<li>Beachcombing</li>\n	<li>Boating</li>\n	<li>Bird Watching</li>\n	<li>Gambling Casinos</li>\n	<li>Museums</li>\n	<li>Restaurants</li>\n	<li>Sight Seeing</li>\n	<li>Shopping</li>\n</ul>\n</div>", "Activities", "", "inherit", "open", "open", "", "21-autosave-v1", "", "", "2013-08-24 07:21:20", "2013-08-24 07:21:20", "", "21", "http://localhost/pw-house/wordpress/21-autosave-v1/", "0", "revision", "", "0");
