INSERT INTO `wp_posts` VALUES("222", "1", "2013-08-27 23:21:51", "2013-08-27 23:21:51", "<img alt=\"\" src=\"/wp-content/themes/pointwhite/img/pw-ferry-wide.jpg\" />\r\n<div class=\"block-text\">\r\n<h2>Just the right distance away</h2>\r\n<p>Point White Guest House features an abundance of materials for reading, writing, sketching, stargazing and dreaming. The cottage is set in a secluded garden and shares 180 feet of waterfront. It’s a birder’s paradise, with kingfishers, redwing blackbirds, eagles, osprey, blue herons, pipers, swallows and songbirds. You might encounter otters, sea lions and even the occasional orca pod.</p>\r\n<p>The location is a short drive from downtown Bainbridge Island and the ferry dock. The island is a 35 minute ferry ride from downtown Seattle. There is bus and taxi service throughout the island.</p>\r\n<h4>Amenities</h4>\r\n<ul class=\"text-cols--2\">\r\n	<li>Cable TV</li>\r\n	<li>WiFi</li>\r\n	<li>Refrigerator</li>\r\n	<li>Microwave</li>\r\n	<li>Outdoor grill</li>\r\n	<li>Washer/dryer</li>\r\n	<li>Coffee service</li>\r\n	<li>Kettle and toaster</li>\r\n	<li>Iron/ironing board</li>\r\n	<li>Tandem bike</li>\r\n	<li>Designated parking</li>\r\n</ul>\r\n</div>", "About", "", "inherit", "open", "open", "", "146-revision-v1", "", "", "2013-08-27 23:21:51", "2013-08-27 23:21:51", "", "146", "http://localhost/pw-house/wordpress/146-revision-v1/", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("223", "1", "2013-08-27 23:23:14", "2013-08-27 23:23:14", "<img alt=\"\" src=\"/pw-house/wordpress/wp-content/themes/pointwhite/img/pw-beach-wide.jpg\" />\r\n<div class=\"block-text\">\r\n<h2>A wealth of things to do</h2>\r\n<p>Hike the shore-to-forest trails of the neighborhood or jump on your mountain bike. Walk along the scenic beach or ride the double bike to shops, restaurants, entertainment and amenities including a gourmet bakery and deli, a luxurious day spa, live music at The Treehouse Café, a neighborhood convenience shop, the 1912 Manor House and five-star Beach House Restaurant, the Pleasant Beach Village and the Historic Lynnwood Theatre, a vintage venue dating back to 1936. Enjoy the seasonal Sunday Market in the summer months, pick wild berries and apples in the fall, or settle in for winter stormwatching.</p>\r\n<h4>Activities</h4>\r\n<ul class=\"text-cols--2\">\r\n	<li>Fire pit</li>\r\n	<li>Mountain biking</li>\r\n	<li>Fort Ward State Park</li>\r\n	<li>Golf</li>\r\n	<li>Horseback riding</li>\r\n	<li>Wildlife viewing</li>\r\n	<li>Swimming</li>\r\n	<li>Equestrian events</li>\r\n	<li>Sound/bay fishing</li>\r\n	<li>Sailing</li>\r\n	<li>Cycling</li>\r\n	<li>Hiking</li>\r\n	<li>Kayaking</li>\r\n	<li>Walking</li>\r\n	<li>Health/beauty spa</li>\r\n	<li>Winery tours</li>\r\n	<li>Botanical garden</li>\r\n	<li>Cinemas</li>\r\n	<li>Marina</li>\r\n	<li>Library</li>\r\n	<li>Live theater</li>\r\n	<li>Beachcombing</li>\r\n	<li>Boating</li>\r\n	<li>Bird watching</li>\r\n	<li>Gambling casinos</li>\r\n	<li>Museums</li>\r\n	<li>Restaurants</li>\r\n	<li>Sight seeing</li>\r\n	<li>Shopping</li>\r\n</ul>\r\n</div>", "Activities", "", "inherit", "open", "open", "", "21-revision-v1", "", "", "2013-08-27 23:23:14", "2013-08-27 23:23:14", "", "21", "http://localhost/pw-house/wordpress/21-revision-v1/", "0", "revision", "", "0");
